CREATE TABLE [dbo].[sdb0country] (
    country_id      UNIQUEIDENTIFIER NOT NULL DEFAULT NEWSEQUENTIALID() PRIMARY KEY,
    iso_code        VARCHAR(3)        NOT NULL UNIQUE, -- ISO 二碼
    iso3_code       VARCHAR(3)        NOT NULL,         -- ISO 三碼
    country_name_zh NVARCHAR(50)      NOT NULL,          -- 國家中文名稱
    country_name_en VARCHAR(100)      NOT NULL,          -- 國家英文名稱
    phone_code      VARCHAR(10)       NULL,              -- 國際電話碼
    currency_code   VARCHAR(3)        NULL,              -- 幣別代碼
    is_active       BIT               NOT NULL DEFAULT 1,-- 是否啟用
    created_at      DATETIME2         NOT NULL DEFAULT SYSUTCDATETIME(), -- 建立時間
    created_by      VARCHAR(50)       NULL,              -- 建立者
    updated_at      DATETIME2         NULL,              -- 更新時間
    updated_by      VARCHAR(50)       NULL               -- 更新者
);

